```JS
function compose(...funcs) {
  return function (v) {
    return funcs.reduce((p, cb) => {
      return p.then(() => cb)
    }, v)
  }
}

class Koa {
  constructor() {
    this.midwares = []
  }
  compose() {
    const resolve = () => {}
    const reject = () => {}
    const dispatch = (i) => {
      const fn = this.midwares[i]
      if (!fn) return Promise.resolve()
      return Promise.resolve(
        fn(() => {
          return dispatch(i + 1)
        })
      )
    }
    return dispatch(0)
  }
  use(cb) {
    this.midwares.push(cb)
  }
}

