function deepCopy(obj, map = new Map()) {
  let copy
  switch (typeof obj) {
      case 'undefined':
        break;
      case 'number':
      case 'boolean':
        copy = obj
        break;
      case 'string':
        copy = obj + ''
        break;
      case 'bigint':
        copy = BigInt(obj)
        break;
      case 'object':
        if (obj === null)
          copy = null
        else {
          const clone = map.get(obj)
          if (clone) {
              return clone
          }
          // new Boolean,Number ä¸å¤„ç†
          if (Object.prototype.toString.call(obj) === '[object Array]') {
            const len = obj.length;
            copy = new Array(len)
            map.set(obj, copy)
            for (let i = 0; i < len; i++) {
              copy[i] = deepCopy(obj[i], map)
            }
          }
          else {
            copy = {}
            map.set(obj, copy)
            for(const k in obj) {
              obj.hasOwnProperty(k) && (copy[k] = deepCopy(obj[k], map))
            }
          }
        }
        break;
      case 'symbol':
        copy = Symbol(obj.description)
        break;
      case 'function':
        const str = obj.toString()
        if (!str.includes('native code')) {
          copy = Function(str)
        }
        break
  }
  return copy
}
