```typescript
/**
 * è‡ªå®šä¹‰åˆ¤æ–­ç±»å‹å‡½æ•°
 *
 * @export
 * @param {*} value
 * @return {*}  {string}
 */
export function typeOf(value: any): string {
  const type = typeof value
  switch (type) {
    case 'string':
    case 'boolean':
    case 'bigint':
    case 'number':
    case 'symbol':
    case 'function':
    case 'undefined':
      /* eslint-disable no-self-compare */
      if (value !== value) {
        return 'NaN'
      }
      return type
    case 'object':
      if (value === null) {
        return 'null'
      }
      if (isObject(value)) {
        return 'object'
      }
      if (Array.isArray(value)) {
        return 'Array'
      }
  }
  return ''
}
