### æ·±åº¦æ¯”è¾ƒ

```typescript
/**
 * æ·±åº¦æ¯”è¾ƒå‡½æ•°ï¼Œä¸èƒ½æ¯”è¾ƒæœ‰å¾ªç¯å¼•ç”¨çš„å¯¹è±¡
 *
 * @export
 * @param {*} value
 * @param {*} other
 * @return {*}  {boolean}
 */
export function deepEqual(value: any, other: any): boolean {
  if (value === other) {
    return true
  }
  const typeA = typeOf(value)
  const typeB = typeOf(other)
  if (typeA !== typeB) {
    return false
  }
  switch (typeA) {
    case 'Array':
      if (value.length !== other.length) {
        return false
      }
      for (let i = 0, len = value.length; i < len; i++) {
        if (!deepEqual(value[i], other[i])) {
          return false
        }
      }
      return true
    case 'object':
      for (const key of Object.keys(value)) {
        if (!deepEqual(value[key], other[key])) {
          return false
        }
      }
      return true
  }
  return value === other
}
```

