 * @returns
 */
export function shuffle<T = any>(shuffleArray: T[]): T[] {
  for (let i = shuffleArray.length - 1; i >= 0; i--) {
    let ri = Math.floor(Math.random() * (i + 1))
    let temp = shuffleArray[ri]
    shuffleArray[ri] = shuffleArray[i]
    shuffleArray[i] = temp
  }
  return shuffleArray
}
