const {src, dest, parallel, task, series} = require('gulp');
const gulp = require('gulp');
const uglify = require('gulp-uglify');
const rename = require('gulp-rename');
const concat = require('gulp-concat');
const htmlmin = require('gulp-htmlmin');
const cleanDest = require('gulp-clean-dest');
const cleanCss = require('gulp-clean-css');
const gulpCopy = require('gulp-copy');
const connect = require('gulp-connect')
const livereload = require('gulp-livereload')


function watch() {
  livereload.listen({}, server)
  gulp.watch('gulpfile.js', series(clean, copy, js, css, html))
  gulp.watch('src/**/*.js', parallel(js))
  gulp.watch('src/**/*.css', parallel(css))
  gulp.watch('src/**/*.html', parallel(html))

}

function server() {
  connect.server({
    root: 'build',
    port: 8000,
    livereload: true
  })
}

function clean() {
  return src('build/**',)
    .pipe(cleanDest('build', {extension: ''}))

}

function copy() {
  return src('src/*.png',)
    .pipe(gulpCopy('', {}))
    .pipe(dest('build'))

}

function html() {
  return src('src/**/*.html')
    .pipe(htmlmin({collapseWhitespace: true}))   //å»é™¤ç©ºæ ¼
    .pipe(dest('build/'))
    .pipe(livereload())
    .pipe(connect.reload())
}


function css() {
  return src('src/**/*.css')
    .pipe(concat('main.css'))
    .pipe(cleanCss())
    .pipe(dest('build/'))
    .pipe(livereload())
    .pipe(connect.reload())
}


function js() {
  return src('src/**/*.js')
    .pipe(concat('main.js'))
    .pipe(uglify())
    .pipe(rename({suffix: '.min'}))
    .pipe(dest('build/'))
    .pipe(livereload())
    .pipe(connect.reload())
}

task('clean', clean)
task('copy', copy)
task('watch', watch)
task('js', js)
task('css', css)
task('html', html)
task('server', series(watch, server))

exports.default = series(clean, parallel(copy, html, js, css));

```

